<script src='https://npmcdn.com/flickity@2/dist/flickity.pkgd.js'></script>
<script>
    $(document).ready(function(){
        var current = 1;
        widget      = $(".step");
        btnnext     = $(".next");
        btnback     = $(".back");
        btnsubmit   = $(".submit");
        $("form input[type=radio]").on('click', function(e) {
            $(".show_step_count .current_step").text(current);
            $(".show_step_count .total_step").text(widget.length);
            if(current < widget.length){
                // Check validation
                widget.show();
                widget.not(':eq('+(current++)+')').hide();
                setProgress(current);
            }
            hideButtons(current);
        });
        $(".show_step_count .current_step").text(current);
        $(".show_step_count .total_step").text(widget.length);
        // Init buttons and UI
        widget.not(':eq(0)').hide();
        hideButtons(current);
        setProgress(current);
        // Next button click action
        btnnext.click(function(){
            $(".show_step_count .current_step").text(current);
            $(".show_step_count .total_step").text(widget.length);
            if(current < widget.length){
                // Check validation
                widget.show();
                widget.not(':eq('+(current++)+')').hide();
                setProgress(current);
            }
            hideButtons(current);
        })
        // Back button click action
        btnback.click(function(){
            if(current > 1){
                current = current - 2;
                if(current < widget.length){
                    widget.show();
                    widget.not(':eq('+(current++)+')').hide();
                    setProgress(current);
                }
            }
            hideButtons(current);
        })
    });
    // Change progress bar action
    setProgress = function(currstep){
        var percent = parseFloat(100 / widget.length) * currstep;
        percent = percent.toFixed();
        $(".progress-bar").css("width",percent+"%").html(percent+"%");
    }
    // Hide buttons according to the current step
    hideButtons = function(current){
        var limit = parseInt(widget.length);
        $(".action").hide();
        if(current < limit) btnnext.show();
        if(current > 1) btnback.show();
        if (current == limit) {
            // Show entered values
            $(".display label:not(.control-label)").each(function(){
                console.log($(this).find("label:not(.control-label)").html($("#"+$(this).data("id")).val()));
            });
            btnnext.hide();
            btnsubmit.show();
        }
    }
</script>

